import React from 'react';
import { View, Text, Button, StyleSheet } from 'react-native';
import MainLayout from '../layouts/MainLayout'; // Import the MainLayout component

function HomeScreen({ navigation }) {
  return (
    <MainLayout>
      <View style={styles.container}>
        <Text style={styles.title}>Welcome to My To-Do App</Text>
        <Text style={styles.subtitle}>Get organized and manage your tasks!</Text>
        <Button
          title="Go to To-Do List"
          onPress={() => navigation.navigate('ToDoList')} // Navigate to ToDoList
        />
        <Button
          title="Go to About"
          onPress={() => navigation.navigate('About')}
        />
      </View>
    </MainLayout>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 20,
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  subtitle: {
    fontSize: 16,
    marginBottom: 20,
  },
});

export default HomeScreen;
